import React from 'react';


export default function AddQuestionButton ({questionAll, handleAdd, questionsArray, setQuestionsArray}) {

    useEffect()

    function handleAdd(e) {
        e.preventDefault();
        questionAll.push("This is my question")
        alert ("Question added")
    }


    return (
        <>
            <button className='btn rounded bg-dark text-white p-2 m-2' onClick={handleAdd}>Add Item</button>
        </>
    )
}
