import React, { useState } from 'react';
import FormPreviewButton from './FormPreviewButton';
import AddQuestionButton from './AddQuestionButton';
import MainNav from './MainNav';


const questions = [
    {
        label: "Radio",
        value: "radio",
    },

    {
        label: "Multiple Choice",
        value: "multiple choice",
    },

    {
        label: "Open Ended (short)",
        value: "open ended short",
    },

    {
        label: "Open Ended (long)",
        value: "open ended long",
    }


]

export default function FormCreation() {
    const [formTitle, setFormTitle] = useState('Untitled');
    const [formDescription, setFormDescription] = useState(' ')
    const [question, setQuestion] = useState("radio")

    return (
        <>
            <MainNav />
            <div className='container mt-4'>
            <h1 className="h1 display-5">Formidable Form Creator</h1>
            <input type="text" placeholder="Form Title" onChange={(e) => setFormTitle(e.target.value)} /><br />
            <input type="text" placeholder="Form Description" onChange={(e) => setFormDescription(e.target.value)}  />
            <form>
                <select name="questions" id="questions" value={question} onChange={(e) => setQuestion(e.target.value)} >
                    {questions.map((question) => (
                        <option value={question.value}>{question.label}</option>
                    ))}
                </select><br />
                <AddQuestionButton /><br />
                <FormPreviewButton />
                <button>Submit Form</button>
            </form>
         <p className="text-danger">{question}</p>
         <p className="text-danger">{formTitle}</p>
         </div>

        </>
    )
}
