import OpenEndedLong from './OpenEndedLong';
import OpenEndedShort from './OpenEndedShort';
import Radio from './Radio';
import Checkbox from './Checkbox';
import MultipleChoices from './MultipleChoices';

export default function Questions({isSelected, setIsSelected, questions, question, setQuestion}) {

     function handleActive(e) {
        e.preventDefault();
        setIsSelected({

        })
     }

    return (
        <>
        <p>Select Question Type</p>
                <select className="form-control" id={question.name} value={question} onChange={(e) => setQuestion(e.target.value)} >
                    {questions.map((question) => (
                        <option value={question.value} onClick={handleActive} >{question.label}</option>
                    ))}
                </select><br />
           <div className="mb-3 bg-light">
                    {
                    isSelected.isActive && isSelected.name === "openEndedShortName" ? (<OpenEndedShort />): isSelected.isActive && isSelected === "Radio" ? (<Radio />):
                     isSelected.isActive && isSelected.name === "openEndedLongName" ? (<OpenEndedLong />): isSelected.isActive && isSelected.name === "checkboxName" ? (<Checkbox />): (<MultipleChoices />)
                     }
                </div>
        </>
    )

}
